        private uint _NPCID;
        public void OnNPCIDChanged(uint oValue, uint nValue)
        {
            if (MapControl?.Objects == null || NPCQuestBox == null || MeiriNPCQuestBox == null) return;

            foreach (MapObject ob in MapControl.Objects)
            {
                if (ob.Race != ObjectType.NPC || ob.ObjectID != NPCID) continue;

                MeiriNPCQuestBox.NPCInfo = NPCQuestBox.NPCInfo = ((NPCObject)ob).NPCInfo;
                return;
            }
            MeiriNPCQuestBox.NPCInfo = NPCQuestBox.NPCInfo = null;

        }

