        public override void Process()
        {
            base.Process();

            if (CEnvir.Now >= MoveTime)
            {
                MoveTime = CEnvir.Now.AddMilliseconds(100);
                MapControl.Animation++;
                MoveFrame = true;
            }
            else
                MoveFrame = false;

            #region 平滑绘制

            if (CEnvir.Now >= SubMoveTime)
            {
                SubMoveTime = CEnvir.Now.AddMilliseconds(100 / Config.SmoothRenderingRate);
                SubMoveFrame = true;
            }
            else
            {
                SubMoveFrame = false;
            }

            #endregion

            if (MouseControl == MapControl)
                MapControl.CheckCursor();

            if (MouseControl == MapControl)
            {
                if (CEnvir.Ctrl && MapObject.MouseObject?.Race == ObjectType.Item)
                    MouseItem = ((ItemObject)MapObject.MouseObject).Item;
                else
                    MouseItem = null;
            }


            TimeSpan ticks = CEnvir.Now - ItemTime;
            ItemTime = CEnvir.Now;

            if (!User.InSafeZone)
            {
                foreach (ClientUserItem item in Equipment)
                {
                    if ((item?.Flags & UserItemFlags.Expirable) != UserItemFlags.Expirable) continue;

                    item.ExpireTime -= ticks;
                }

                foreach (ClientUserItem item in Inventory)
                {
                    if ((item?.Flags & UserItemFlags.Expirable) != UserItemFlags.Expirable) continue;

                    item.ExpireTime -= ticks;
                }

                if (Companion != null)
                {
                    foreach (ClientUserItem item in Companion.InventoryArray)
                    {
                        if ((item?.Flags & UserItemFlags.Expirable) != UserItemFlags.Expirable) continue;

                        item.ExpireTime -= ticks;
                    }
                    foreach (ClientUserItem item in Companion.EquipmentArray)
                    {
                        if ((item?.Flags & UserItemFlags.Expirable) != UserItemFlags.Expirable) continue;

                        item.ExpireTime -= ticks;
                    }
                }
            }

            if (MouseItem != null && CEnvir.Now > ItemRefreshTime)
            {
                CreateItemLabel();
            }

            MapControl.ProcessInput();
            

            foreach (ParticleEngine engine in ParticleEngines)
                engine.Update();

            foreach (MapObject ob in MapControl.Objects)
                ob.Process();

            for (int i = MapControl.Effects.Count - 1; i >= 0; i--)
                MapControl.Effects[i].Process();

            if (Config.是否开启粒子效果)
            {
                MapControl.m_xFlyingTail.UpdateSystem((int)ticks.TotalMilliseconds, new Vector3(0.0f, 0.0f, 0.0f));
                MapControl.m_xSmoke.UpdateSystem((int)ticks.TotalMilliseconds, new Vector3(0.0f, 0.0f, 0.0f));
                MapControl.m_xBoom.UpdateSystem((int)ticks.TotalMilliseconds, new Vector3(0.0f, 0.0f, 0.0f));
                if (CEnvir.Now >= ParticleRenderTime)
                {
                    ParticleRenderTime = CEnvir.Now.AddMilliseconds(20.0);
                    if (MapControl.m_bShowSnow)
                        MapControl.m_xSnow.UpdateSystem((int)ticks.TotalMilliseconds, new Vector3(0.0f, 0.0f, 0.0f));
                    if (MapControl.m_bShowRain)
                        MapControl.m_xRain.UpdateSystem((int)ticks.TotalMilliseconds, new Vector3(0.0f, 0.0f, 0.0f));
                }
                MapControl.m_xScatter.UpdateSystem((int)ticks.TotalMilliseconds, new Vector3(0.0f, 0.0f, 0.0f));
            }

            if (ItemLabel != null && !ItemLabel.IsDisposed)
            {
                int x = CEnvir.MouseLocation.X + 15, y = CEnvir.MouseLocation.Y;

                if (x + ItemLabel.Size.Width > Size.Width + Location.X)
                    x = Size.Width - ItemLabel.Size.Width + Location.X;

                if (y + ItemLabel.Size.Height > Size.Height + Location.Y)
                    y = Size.Height - ItemLabel.Size.Height + Location.Y;

                if (x < Location.X)
                    x = Location.X;

                if (y <= Location.Y)
                    y = Location.Y;


                ItemLabel.Location = new Point(x, y);
            }

            if (MagicLabel != null && !MagicLabel.IsDisposed)
            {
                int x = CEnvir.MouseLocation.X + 15, y = CEnvir.MouseLocation.Y;

                if (x + MagicLabel.Size.Width > Size.Width + Location.X)
                    x = Size.Width - MagicLabel.Size.Width + Location.X;

                if (y + MagicLabel.Size.Height > Size.Height + Location.Y)
                    y = Size.Height - MagicLabel.Size.Height + Location.Y;

                if (x < Location.X)
                    x = Location.X;

                if (y <= Location.Y)
                    y = Location.Y;


                MagicLabel.Location = new Point(x, y);
            }

            MonsterObject mob = MouseObject as MonsterObject;
            
            if (Config.开始挂机)
            {
                if (TargetObject == null || TargetObject.Dead)
                {
                    if (Game.User.XunzhaoGuaiwuMoshi01)
                        TargetObject = MapControl.LaoSelectMonster();
                    else if  (Game.User.XunzhaoGuaiwuMoshi02)
                        TargetObject = MapControl.SelectMonster();
                    else if (!Game.User.XunzhaoGuaiwuMoshi01 && !Game.User.XunzhaoGuaiwuMoshi02)
                        TargetObject = MapControl.LaoSelectMonster();

                    if (TargetObject != null)
                        Game.MapControl.AutoPath = false;
                    mob = TargetObject as MonsterObject;
                }
                else
                    mob = TargetObject as MonsterObject;
                MouseObject = TargetObject;
            }
            
            if (mob != null && mob.CompanionObject == null)
                MonsterBox.Monster = mob;
            else
            {
                mob = FocusObject as MonsterObject;
                if (mob != null && mob.CompanionObject == null && !FocusObject.Dead)
                    MonsterBox.Monster = mob;
            }

            
            if (GameScene.Game.FangGuajiBox != null && GameScene.Game.FangGuajiBox.Visible)
            {
                if (Game.FangGuajiBox.ShowTimeSpan > CEnvir.Now)
                {
                    Game.FangGuajiBox.ShowTimeLabel.Text = string.Format("请注意，剩余回答时间为{0:#,##0.0}秒", (object)(GameScene.Game.FangGuajiBox.ShowTimeSpan - CEnvir.Now).TotalSeconds);
                }
                else
                {
                    Game.FangGuajiBox.Visible = false;
                    Game.FangGuajiBox.Label.Text = "目前没有验证内容";
                    Game.FangGuajiBox.ShowTimeLabel.Text = "";
                    Game.FangGuajiBox.ConfirmButton.Enabled = false;
                }
            }
            
            if (MonsterBox.Monster != null && !MonsterBox.Monster.Dead)
            {
                if ((MonsterBox.Monster.Poison & PoisonType.Green) == PoisonType.Green && (MonsterBox.Monster.Poison & PoisonType.Red) != PoisonType.Red)
                    MonsterBox.NameLabel.ForeColour = Color.Lime;
                else if ((MonsterBox.Monster.Poison & PoisonType.Red) == PoisonType.Red && (MonsterBox.Monster.Poison & PoisonType.Green) != PoisonType.Green)
                    MonsterBox.NameLabel.ForeColour = Color.Coral;
                else if ((MonsterBox.Monster.Poison & PoisonType.Red) == PoisonType.Red && (MonsterBox.Monster.Poison & PoisonType.Green) == PoisonType.Green)
                    MonsterBox.NameLabel.ForeColour = Color.Yellow;
                else if ((MonsterBox.Monster.Poison & PoisonType.Green) != PoisonType.Green && (MonsterBox.Monster.Poison & PoisonType.Red) != PoisonType.Red)
                    MonsterBox.NameLabel.ForeColour = Color.White;
            }

            if (TipWarning != null && CEnvir.Now - TipWarning.StartTime > TipWarning.Duration)
            {
                TipWarning.Label.Visible = false;
            }
            if (MapChanged != null && CEnvir.Now - MapChanged.StartTime > MapChanged.Duration)
            {
                MapChanged.Label.Visible = false;
            }

            AutoZidongGuajiChanged();

            InventoryBox.InventoryDialogProcess();

            if (!Observer)
                BigPatchBox?.UpdateAutoAssist();

            ProcessSkills();
        }
