        private void CreateItemLabel()

        {
            Type itemType;
            MemberInfo[] infos;
            DescriptionAttribute description;

            if (ItemLabel != null && !ItemLabel.IsDisposed) ItemLabel.Dispose();

            if (MouseItem == null) return;

            ItemRefreshTime = CEnvir.Now.AddSeconds(1);

            Stats stats = new Stats();
            stats.Add(MouseItem.Info.Stats);
            stats.Add(MouseItem.AddedStats);

            ItemLabel = new DXControl
            {
                BackColour = Color.FromArgb(200, 39, 35, 25),
                Border = true,
                BorderColour = Color.FromArgb(100, 90, 75), 
                DrawTexture = true,
                IsControl = false,
                IsVisible = true,
            };

            ItemInfo displayInfo = MouseItem.Info;

            
            itemImage = new DXImageControl
            {
                LibraryFile = LibraryFile.Inventory,
                Index = displayInfo.Image,
                Parent = ItemLabel,
                IsControl = false,
            };
            LeftOffset = itemImage.Size.Width + 20;

            if (MouseItem.Info.Effect == ItemEffect.ItemPart)
                displayInfo = CartoonGlobals.ItemInfoList.Binding.First(x => x.Index == MouseItem.AddedStats[Stat.ItemIndex]);
            
            if ((MouseItem.BaoshiMaEr & BaoshiMaEr.Youliang) != BaoshiMaEr.Youliang & (MouseItem.BaoshiMaEr & BaoshiMaEr.Jingzhi) != BaoshiMaEr.Jingzhi & (MouseItem.BaoshiMaEr & BaoshiMaEr.Shenhua) != BaoshiMaEr.Shenhua & (MouseItem.BaoshiMaEr & BaoshiMaEr.Chuanshuo) != BaoshiMaEr.Chuanshuo)
            {
                DXLabel labell = new DXLabel
                {
                    ForeColour = Color.Yellow,
                    Location = new Point(LeftOffset, 4),
                    Parent = ItemLabel,
                    Text = displayInfo.ItemName
                };

                if (MouseItem.Info.Effect == ItemEffect.ItemPart)
                    labell.Text += " - [碎片]";
                ItemLabel.Size = new Size(labell.DisplayArea.Right + 4, labell.DisplayArea.Bottom);

                
                /*
                if (displayInfo.Rarity == Rarity.Elite)
                    labell.ForeColour = Color.MediumPurple;
                else if (displayInfo.Rarity == Rarity.Superior)
                    labell.ForeColour = Color.MediumSpringGreen;
                else
                    labell.ForeColour = Color.White;
                */
            }
            else if ((MouseItem.BaoshiMaEr & BaoshiMaEr.Youliang) == BaoshiMaEr.Youliang)
            {
                DXLabel labell = new DXLabel
                {
                    ForeColour = Color.Lime,
                    Location = new Point(LeftOffset, 4),
                    Parent = ItemLabel,
                    Text = "优良" + displayInfo.ItemName
                };
                if (MouseItem.Info.Effect == ItemEffect.ItemPart)
                    labell.Text += " - [碎片]";
                ItemLabel.Size = new Size(labell.DisplayArea.Right + 4, labell.DisplayArea.Bottom);
            }
            else if ((MouseItem.BaoshiMaEr & BaoshiMaEr.Jingzhi) == BaoshiMaEr.Jingzhi)
            {
                DXLabel labell = new DXLabel
                {
                    ForeColour = Color.MediumOrchid,
                    Location = new Point(LeftOffset, 4),
                    Parent = ItemLabel,
                    Text = "精致" + displayInfo.ItemName
                };
                if (MouseItem.Info.Effect == ItemEffect.ItemPart)
                    labell.Text += " - [碎片]";
                ItemLabel.Size = new Size(labell.DisplayArea.Right + 4, labell.DisplayArea.Bottom);
            }
            else if ((MouseItem.BaoshiMaEr & BaoshiMaEr.Chuanshuo) == BaoshiMaEr.Chuanshuo)
            {
                DXLabel labell = new DXLabel
                {
                    ForeColour = Color.Goldenrod,
                    Location = new Point(LeftOffset, 4),
                    Parent = ItemLabel,
                    Text = "传说" + displayInfo.ItemName
                };
                if (MouseItem.Info.Effect == ItemEffect.ItemPart)
                    labell.Text += " - [碎片]";
                ItemLabel.Size = new Size(labell.DisplayArea.Right + 4, labell.DisplayArea.Bottom);
            }
            else if ((MouseItem.BaoshiMaEr & BaoshiMaEr.Shenhua) == BaoshiMaEr.Shenhua)
            {
                DXLabel labell = new DXLabel
                {
                    ForeColour = Color.Coral,
                    Location = new Point(LeftOffset, 4),
                    Parent = ItemLabel,
                    Text = "神话" + displayInfo.ItemName
                };
                if (MouseItem.Info.Effect == ItemEffect.ItemPart)
                    labell.Text += " - [碎片]";
                ItemLabel.Size = new Size(labell.DisplayArea.Right + 4, labell.DisplayArea.Bottom);
            }

            
            if (MouseItem.CraftInfoOnly)
            {
                DXLabel dXLabel = new DXLabel
                {
                    ForeColour = Color.Yellow,
                    Location = new Point(LeftOffset, ItemLabel.DisplayArea.Bottom),
                    Parent = ItemLabel,
                    Text = $"需要数量: {MouseItem.Count}"
                };
                ItemLabel.Size = new Size((dXLabel.DisplayArea.Right + 4 > ItemLabel.Size.Width) ? (dXLabel.DisplayArea.Right + 4) : ItemLabel.Size.Width, (dXLabel.DisplayArea.Bottom > ItemLabel.Size.Height) ? dXLabel.DisplayArea.Bottom : ItemLabel.Size.Height);
                
                return;
