            if ((MouseItem.BaoshiMaEr & BaoshiMaEr.Youliang) == BaoshiMaEr.Youliang)
            {
                label = new DXLabel
                {
                    ForeColour = Color.Lime,
                    Location = new Point(LeftOffset, ItemLabel.DisplayArea.Bottom),
                    Parent = ItemLabel,
                    Text = "",
                    
