        public void FillItems(List<ClientUserItem> items)
        {
            foreach (ClientUserItem item in items)
            {
                if (item.Slot >= 3000)
                {
                    int index = item.Slot - 3000;
                    if (InventoryBox.BaoshiGrid.Grid.Length > index)
                        InventoryBox.BaoshiGrid.Grid[index].Item = item;
                }
                else if (item.Slot >= 2000)
                {
                    int index = item.Slot - 2000;
                    if (InventoryBox.PatchGrid.Grid.Length > index)
                        InventoryBox.PatchGrid.Grid[index].Item = item;
                }
                else if (item.Slot >= 1000)
                {
                    int index = item.Slot - 1000;
                    if (CharacterBox.Grid.Length > index)
                        CharacterBox.Grid[index].Item = item;
                }
                else if (InventoryBox.Grid.Grid.Length > item.Slot)
                    InventoryBox.Grid.Grid[item.Slot].Item = item;
            }
        }


